(9, 'profile', '\r\n<!DOCTYPLE html>\r\n<html>\r\n<head>\r\n    <meta charset=\"utf-8\">\r\n      <link rel=\"stylesheet\" type=\"text/css\" href=\"../style.css\"> \r\n           <link rel=\"stylesheet\" type=\"text/css\" href=\"profile.css\">\r\n    <link href=\"https://fonts.googleapis.com/css?family=IBM+Plex+Sans&display=swap\" rel=\"stylesheet\">\r\n</head>\r\n    <body>\r\n        <div id = \"profWrapper\">\r\n            <div id = \"profileBody\"> \r\n            </div>\r\n             <div id = \"textArea\">Grades </div>\r\n            <div id = \"gradeBox\"></div>\r\n        </div>\r\n    </body>\r\n</html>\r\n\r\n', '<style>\r\n#profWrapper{\r\n    position: relative;\r\n    background-color: black;\r\n    width: 100%;\r\n    height: 1000px;\r\n}\r\n#profileBody{\r\n    background-image: linear-gradient(#8CCAC9, #74BEA5,#9ABF7A,#E0AA2E);\r\n    width: 100%;\r\n    height: 250px;\r\n    position: absolute;\r\n    top: 0%;\r\n    left: 0%;\r\n}\r\n#textArea{\r\n    position: absolute;\r\n    left: 38%;\r\n    top: 10%;\r\n    text-align: center;\r\n    font-family: \'IBM Plex Sans\', sans-serif;\r\n    font-size: 35pt;\r\n    color: white;\r\n    font-weight: lighter;\r\n    background-color: transparent;\r\n}\r\n#gradeBox{\r\n    position: absolute;\r\n    top: 30%;\r\n    width: 90%;\r\n    height: 600px;\r\n    left: 5%;\r\n    background-color:#ADE4E8;\r\n    \r\n}\r\n</style>'),
(10, 'tutorial', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n  <head>\r\n    <title>Tutorial</title>\r\n    <meta charset=\"UTF-8\" />\r\n    <meta name=\"viewport\" content=\"initial-scale=1.0\" />\r\n    <link rel=\"stylesheet\" href=\"Tutorial.css\" />\r\n    <link\r\n      rel=\"stylesheet\"\r\n      href=\"https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css\"\r\n    />\r\n    <link rel=\"stylesheet\" href=\"../style.css\" />\r\n    <link\r\n      href=\"https://fonts.googleapis.com/css?family=Fira+Code&display=swap\"\r\n      rel=\"stylesheet\"\r\n    />\r\n    <script>\r\n      (function(window, document) {\r\n        window.onload = init;\r\n        function getResult() {\r\n          var answer1 = document.getElementById(\"q1\").value;\r\n          var answer2 = document.getElementById(\"q2\").value;\r\n          var result1 = document.getElementById(\"result1\");\r\n          var result2 = document.getElementById(\"result2\");\r\n          if (answer1.length) {\r\n            if (answer1 == \"0\") {\r\n              result1.innerHTML = \'<p class=\"correct\">You got the answer!</p>\';\r\n            } else {\r\n              result1.innerHTML = \'<p class=\"wrong\">Sorry try again!</p>\';\r\n            }\r\n          } else {\r\n            result1.innerHTML = \'<p class=\"wrong\">This field is required.</p>\';\r\n          }\r\n          if (answer2.length) {\r\n            if (answer2 == \"222\") {\r\n              result2.innerHTML = \'<p class=\"correct\">You got the answer!</p>\';\r\n            } else {\r\n              result2.innerHTML = \'<p class=\"wrong\">Sorry try again!</p>\';\r\n            }\r\n          } else {\r\n            result2.innerHTML = \'<p class=\"wrong\">This field is required.</p>\';\r\n          }\r\n        }\r\n        function init() {\r\n          var submit = document.getElementById(\"submit\");\r\n          submit.addEventListener(\"click\", getResult);\r\n        }\r\n      })(window, document);\r\n    </script>\r\n  </head>\r\n\r\n  <div id=\"wrapper\">\r\n    <h1>Tutorial</h1>\r\n    <div id=\"boxes\">\r\n      <!-- Part 1 of tutorial -->\r\n      <h2>Setting up your environment</h2>\r\n      <br />\r\n      <br />\r\n      If you are a Macbook User (Unix/Linux is already accesible to you):\r\n      <br /><br />\r\n      <input type=\"checkbox\" id=\"box-1\" />\r\n      <label for=\"box-1\">\r\n        You can find the terminal by going to your utilities folder then\r\n        selecting the terminal.\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-2\" />\r\n      <label for=\"box-2\">\r\n        When you open your terminal you will first see the last login on your\r\n        account as well<br />\r\n        as the prompt which ends in a dollar sign($).\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-3\" />\r\n      <label for=\"box-3\">\r\n        Now you are all set up to use Linux and practice some commands that you\r\n        will see soon.\r\n      </label>\r\n      <br /><br />\r\n      If you are a Windows User (Unix/Linux is not already accesible to you):\r\n      <br /><br />\r\n      <video class=\"video\" width=\"640\" height=\"480\" controls>\r\n        <source src=\"../assets/web&mobilevideo.mp4\" type=\"video/mp4\" />\r\n      </video>\r\n      <br /><br />\r\n\r\n      <input type=\"checkbox\" id=\"box-4\" />\r\n      <label for=\"box-4\"> Go to settings in Windows 10 </label>\r\n      <input type=\"checkbox\" id=\"box-5\" />\r\n      <label for=\"box-5\">\r\n        On the left, you will see for developers click on that button\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-6\" />\r\n      <label for=\"box-6\">\r\n        Now click the button Developer mode, this will take a few mins to\r\n        install\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-7\" />\r\n      <label for=\"box-7\"> Open your control panel </label>\r\n      <input type=\"checkbox\" id=\"box-8\" />\r\n      <label for=\"box-8\"> Click on programs </label>\r\n      <input type=\"checkbox\" id=\"box-9\" />\r\n      <label for=\"box-9\"> Then click turn windows features on or off </label>\r\n      <input type=\"checkbox\" id=\"box-10\" />\r\n      <label for=\"box-10\">\r\n        Scroll down to Windows Subsystem for Linux and check the box and hit\r\n        okay\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-11\" />\r\n      <label for=\"box-11\">\r\n        Once this is done you will have to click restart to restart your\r\n        computer with the new software\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-12\" />\r\n      <label for=\"box-12\">\r\n        Once your computer is restarted then go to windows store and search for\r\n        ubuntu\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-13\" />\r\n      <label for=\"box-13\">Click on ubuntu and install it </label>\r\n      <input type=\"checkbox\" id=\"box-14\" />\r\n      <label for=\"box-14\"> Once it is installed click the launch button </label>\r\n      <input type=\"checkbox\" id=\"box-15\" />\r\n      <label for=\"box-15\">\r\n        It will ask you to create a new Unix username so type that in\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-16\" />\r\n      <label for=\"box-16\"> Then enter a custom password </label>\r\n      <input type=\"checkbox\" id=\"box-17\" />\r\n      <label for=\"box-17\"> Now once that is done you are in Linux </label>\r\n      <input type=\"checkbox\" id=\"box-18\" />\r\n      <label for=\"box-18\">\r\n        type in the changing directory command in order to direct to the desktop\r\n        \'cd /mnt/c/users/ComputerUsername/Desktop\'\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-19\" />\r\n      <label for=\"box-19\">\r\n        Type the previous command out and replace ComputerUsername with whatever\r\n        your username is and press enter\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-20\" />\r\n      <label for=\"box-20\">\r\n        Now you are ready to continue to more tutorials have fun!\r\n      </label>\r\n      <br />\r\n      Once your environment is set up download this file for the rest of the\r\n      tutorial:\r\n      <br /><br />\r\n      <a href=\"Untitled-3.txt\" class=\"buttonDownload\" target=\"_blank\" download>\r\n        <label for=\"box-1\"> </label>\r\n        Click to Download</a\r\n      >\r\n      <br /><br />\r\n      Download the file and place it on your desktop. Once this is done open\r\n      your<br />command terminal that you were able to open based on the\r\n      instructions above<br />\r\n      and get ready for the commands you will be using in the next step to this\r\n      tutorial. <br /><br />\r\n    </div>\r\n    <!-- end of boxes -->\r\n    <br /><br />\r\n    <div id=\"boxes\">\r\n      <!-- Part 2 of tutorial -->\r\n      <h2>Introduction Commands</h2>\r\n      <br />\r\n      <br />\r\n      Using your terminal please follow along with each step to successfully<br />complete\r\n      the tutorial and understand how to use the commands your learned:\r\n      <br /><br />\r\n      <input type=\"checkbox\" id=\"box-21\" />\r\n      <label for=\"box-21\"> Type \'cd desktop\' into the command terminal </label>\r\n      <input type=\"checkbox\" id=\"box-22\" />\r\n      <label for=\"box-22\">\r\n        Type \'mkdir tutorial\' which will make a folder called tutorial\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-23\" />\r\n      <label for=\"box-23\">\r\n        Type \'mv Untitled-3.txt ~/Desktop/tutorial\' in order to move the\r\n        directory into the tutorial folder\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-24\" />\r\n      <label for=\"box-24\">\r\n        Type \'cd tutorial\' This will redirect you into the tutorial folder\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-25\" />\r\n      <label for=\"box-25\">\r\n        Type \'mv Untitled-3.txt BasixUnix.txt\' this will rename the file to\r\n        BasixUnix\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-26\" />\r\n      <label for=\"box-26\">\r\n        In order for you to check that this worked type \'ls *.txt\' if the file\r\n        BasixUnix displayed <br />it was done correctly. If it was not done\r\n        correctly go back to the previous step and try again.\r\n      </label>\r\n      <label for=\"q1\"> What is the size of the folder?&nbsp;&nbsp;</label>\r\n      <input type=\"text\" id=\"q1\" />\r\n      <div id=\"result1\" class=\"result\"><p></p></div>\r\n\r\n      <input type=\"checkbox\" id=\"box-27\" />\r\n      <label for=\"box-27\"\r\n        >Type \'mkdir changesToFile\' which will make a new folder called\r\n        changesToFile\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-28\" />\r\n      <label for=\"box-28\">\r\n        Type \'cp BasixUnix.txt ChangedBasixUnix.txt\' this will create a copy of\r\n        the file named ChangedBasicUnix\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-29\" />\r\n      <label for=\"box-29\"\r\n        >Type \'mv ChangedBasixUnix ~/tutorial/changesToFile\' to move the txt\r\n        file into the newly made folder\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-30\" />\r\n      <label for=\"box-30\"\r\n        >Now move into that the folder changesToFile by typing \'cd\r\n        changesToFile\'\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-31\" />\r\n      <label for=\"box-31\">\r\n        Let\'s say you accidently changed directories into the wrong folder and\r\n        do not know where you are in your file system<br />\r\n        type the command \'ls\' in order to see all the information about the\r\n        folder.\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-32\" />\r\n      <label for=\"box-32\">\r\n        If you ever go too far into a different folder you did not want to be in\r\n        try typing \'cd..\' now you should be back in<br />the tutorial folder\r\n        only and no longer in the changesToFile folder.\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-33\" />\r\n      <label for=\"box-33\">\r\n        Now go back into the changesToFile folder by using the command \'cd\r\n        changesToFile\'\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-34\" />\r\n      <label for=\"box-34\">\r\n        Now we will start looking at file permissions. In order to look at the\r\n        file permission of a file type \'ls -l\'\r\n      </label>\r\n      <br />\r\n      <label for=\"q2\"\r\n        >What are the file permissions of the ChangedBasixUnix.txt\r\n        file?&nbsp;&nbsp;</label\r\n      >\r\n      <input type=\"text\" id=\"q2\" />\r\n      <div id=\"result2\" class=\"result\"><p></p></div>\r\n      <input type=\"checkbox\" id=\"box-35\" />\r\n      <label for=\"box-35\">\r\n        Now in order to allow yourself to access and modify the file you are\r\n        going to want the file permission to be 777 do this<br />\r\n        by typing \'chmod 777/ChangedBasixUnix.txt\'\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-36\" />\r\n      <label for=\"box-36\">\r\n        Now you are able to access and modify the file so type \'nano\r\n        ChangedBasixUnix.txt\'\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-37\" />\r\n      <label for=\"box-37\">\r\n        Type in whatever message you want into this file then press \'Ctl+x\' to\r\n        exit and \'Ctl+Y\' to save changes\r\n      </label>\r\n      <input type=\"checkbox\" id=\"box-38\" />\r\n      <label for=\"box-38\">\r\n        Type \'echo \"$(&lt;changedBaixUnix)\"\'in order to see the file printed in\r\n        the command terminal\r\n      </label>\r\n      <br />\r\n      <button id=\"submit\">Submit to Check</button>\r\n      <br /><br />\r\n      You have now finished the tutorial Congratulations! Feel free to try the\r\n      tutorial again to practice<br />and if you feel confident enough please\r\n      proceed to take the quizes on the quiz page.\r\n    </div>\r\n    <!-- end of boxes -->\r\n  </div>\r\n  <!-- end of wrapper -->\r\n</html>', '<style>\r\n/*Change all px to em */\r\n#containerQuiz {\r\n  width: 80%;\r\n  margin: 0 auto;\r\n  background: #fff;\r\n  padding: 1em;\r\n  /*  box-shadow: 0 3px 3px rgba(0,0,0,0.2);*/\r\n}\r\nlabel {\r\n  display: inline-block;\r\n  margin-bottom: 10px;\r\n}\r\ninput {\r\n  display: block;\r\n}\r\n#submit {\r\n  display: block;\r\n  margin: 0 auto;\r\n  border: 0;\r\n  color: white;\r\n  background: #4caf50;\r\n  cursor: pointer;\r\n  padding: 0.5em;\r\n}\r\nvideo {\r\n  outline: none;\r\n}\r\n/* labels for Result */\r\n.result p {\r\n  color: #fff;\r\n  padding: 10px;\r\n  border-radius: 5px;\r\n}\r\n.correct {\r\n  background: #2ecc40;\r\n}\r\n.wrong {\r\n  background: #ff4136;\r\n}\r\n/* end of mini quiz */\r\n.container {\r\n  display: block;\r\n  position: relative;\r\n  padding-left: 35px;\r\n  margin-bottom: 12px;\r\n  cursor: pointer;\r\n  font-size: 75%;\r\n  -webkit-user-select: none;\r\n  -moz-user-select: none;\r\n  -ms-user-select: none;\r\n  user-select: none;\r\n}\r\n/* Hide the browser\'s default checkbox */\r\n.container input {\r\n  position: absolute;\r\n  opacity: 0;\r\n  cursor: pointer;\r\n  height: 0;\r\n  width: 0;\r\n  font-size: 75%;\r\n}\r\n/* Create a custom checkbox */\r\n.checkmark {\r\n  position: absolute;\r\n  top: 0;\r\n  left: 0;\r\n  height: 25px;\r\n  width: 25px;\r\n  background-color: #fff;\r\n}\r\n/* On mouse-over, add a grey background color */\r\n.container:hover input ~ .checkmark {\r\n  background-color: #ccc;\r\n}\r\n/* When the checkbox is checked, add a blue background */\r\n.container input:checked ~ .checkmark {\r\n  background-color: #4caf50;\r\n}\r\n/* Create the checkmark/indicator (hidden when not checked) */\r\n.checkmark:after {\r\n  content: \"\";\r\n  position: absolute;\r\n  display: none;\r\n}\r\n/* Show the checkmark when checked */\r\n.container input:checked ~ .checkmark:after {\r\n  display: block;\r\n}\r\n/* Style the checkmark/indicator -- change px to em!!!*/\r\n#wrapper .checkmark:after {\r\n  left: 9px;\r\n  top: 5px;\r\n  width: 5px;\r\n  height: 10px;\r\n  border: solid white;\r\n  border-width: 0 3px 3px 0;\r\n  -webkit-transform: rotate(45deg);\r\n  -ms-transform: rotate(45deg);\r\n  transform: rotate(45deg);\r\n}\r\n/* php will have an external css for the wrapper */\r\n#wrapper {\r\n  min-height: 20vh;\r\n  /*padding: 2vh 3em; */\r\n  color: hsl(0, 100%, 100%);\r\n  font-size: 130%;\r\n  /*    padding: 3em;*/\r\n  background-color: #000;\r\n  font-family: \"IBM Plex Sans\", sans-serif;\r\n}\r\n#wrapper a {\r\n  color: #fff;\r\n}\r\nh1 {\r\n  line-height: 1.5;\r\n  display: inline;\r\n  background-image: linear-gradient(\r\n    transparent 50%,\r\n    #4caf10 50%,\r\n    #4caf50 85%,\r\n    transparent 85%,\r\n    transparent 85%\r\n  );\r\n  background-repeat: no-repeat;\r\n  background-size: 0% 100%;\r\n  animation: animatedBackground 2s cubic-bezier(0.645, 0.045, 0.355, 1) 0.5s\r\n    forwards;\r\n}\r\nh2 {\r\n  line-height: 1.5;\r\n  display: inline;\r\n  background-image: linear-gradient(\r\n    transparent 50%,\r\n    #4caf10 50%,\r\n    #4caf50 85%,\r\n    transparent 85%,\r\n    transparent 85%\r\n  );\r\n  background-repeat: no-repeat;\r\n  background-size: 0% 100%;\r\n  animation: animatedBackground 2s cubic-bezier(0.645, 0.045, 0.355, 1) 0.5s\r\n    forwards;\r\n}\r\n@keyframes animatedBackground {\r\n  to {\r\n    background-size: 100% 100%;\r\n  }\r\n}\r\n.centr {\r\n  text-align: center;\r\n  position: relative;\r\n}\r\n@import url(https://fonts.googleapis.com/css?family=Open+Sans);\r\n/*Page styles*/\r\n#boxes {\r\n  margin: auto;\r\n  padding: 3em;\r\n  /*    background: #484848;*/\r\n  /*font-family: \'Fira Code\', monospace;*/\r\n  border: 0.1875em solid white;\r\n  min-height: 20vh;\r\n  padding: 3em 3em;\r\n  border-image-source: linear-gradi